import { SwipeableDrawer } from '@material-ui/core';
import React, {useState, useEffect} from 'react'
import TinderCard from 'react-tinder-card';
import axios from '../.././axios';
import './TinderCards.css';

function TinderCards() {
    const[people, setPeople] = useState([
        {
            name: 'jennifer law',
            imgUrl: 'https://media1.popsugar-assets.com/files/thumbor/f24SV83aQnTs0oyJZecR7GKBPY0/413x72:2216x1875/fit-in/2048xorig/filters:format_auto-!!-:strip_icc-!!-/2020/06/18/943/n/1922398/6c31632d5eebdeb9cb6252.86135421_/i/Jennifer-Lawrence.jpg'
        }
    ]);

    /*useEffect(() => {
        async function fetchData(){
            const req = await axios.get("/tinder/cards");
            setPeople(req.data);
        }
        fetchData();
    }, []);*/

    console.log('people: ', people);

    const swiped = (direction, nameToDelete) => {
        console.log("Removing: " + nameToDelete);
        //setLastDirection(direction);
    };

    const outOfFrame = (name) => {
        console.log(name + " left the screen!");
    };

    return (
        <div className="tinderCards">
            <div className="tinderCards__cardContainer">
                {people.map((person) => (
                    // TinderCard is a library with Node.js
                    <TinderCard
                        className="swipe"
                        key={person.name}
                        preventSwipe={["up","down"]}
                        onSwipe={(dir) => swiped(dir, person.name)}
                        onCardLeftScreen={() => outOfFrame(person.name)}
                    >
                        <div
                            className="card" 
                            style={{ backgroundImage: 'url(' + person.imgUrl + ')'}}
                        >
                            <h3>{person.name}</h3>
                        </div>
                    </TinderCard>
                ))}
            </div>
        </div>
    );
}

export default TinderCards;

